import React from "react";
import { Button } from "react-bootstrap";

const EmergencyButton = () => {
  const handleClick = () => {
    alert("ðŸš¨ Emergency Alert Sent!");
    // You can add your alert logic here like calling API etc.
  };

  return (
    <div style={styles.container}>
      <Button variant="danger" size="lg" style={styles.button} onClick={handleClick}>
        ðŸš¨ Emergency
      </Button>
    </div>
  );
};

const styles = {
  container: {
    position: "fixed",
    bottom: "20px",
    left: "50%",
    transform: "translateX(-50%)",
    zIndex: 999,
  },
  button: {
    borderRadius: "50px",
    padding: "10px 30px",
    fontWeight: "bold",
    fontSize: "18px",
  },
};

export default EmergencyButton;
