import React, { useState } from 'react';
import { LoadScript, GoogleMap, Marker } from '@react-google-maps/api';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import EmergencyButton from './components/EmergencyButton';

const center = { lat: 17.4239, lng: 78.4862 }; // Hyderabad center

const containerStyle = {
  width: '100%',
  height: '90vh'
};

const hospitals = [
  { name: "KIMS Hospital", location: { lat: 17.4273, lng: 78.4375 } },
  { name: "Ayu Health Hospitals", location: { lat: 17.4148, lng: 78.4487 } },
  { name: "Sunridge Multi Speciality", location: { lat: 17.4489, lng: 78.4601 } }
];

function App() {
  const [showHospitals, setShowHospitals] = useState(false);

  const handleEmergencyClick = () => {
    setShowHospitals(true);
  };

  return (
    <>
      <Navbar />
      <LoadScript googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY}>
        <GoogleMap mapContainerStyle={containerStyle} center={center} zoom={13}>
          {showHospitals && hospitals.map((hospital, index) => (
            <Marker key={index} position={hospital.location} title={hospital.name} />
          ))}
        </GoogleMap>
      </LoadScript>
      <EmergencyButton onClick={handleEmergencyClick} />
      <Footer />
    </>
  );
}

export default App;
