import React from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import EmergencyButton from "./EmergencyButton";

const EmergencyMap = () => {
  const position = [17.385044, 78.486671]; // Sample coordinates

  const handleEmergencyClick = () => {
    alert("Emergency alert sent! Nearest ambulance and hospital notified.");
    // Here you can add further logic (API calls, GPS etc.)
  };

  return (
    <div style={{ height: "100vh", width: "100%" }}>
      <MapContainer center={position} zoom={13} style={{ height: "100%", width: "100%" }}>
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; OpenStreetMap contributors"
        />
        <Marker position={position}>
          <Popup>Current Location</Popup>
        </Marker>
      </MapContainer>

      {/* Emergency Button overlaid on map */}
      <div style={{ position: "absolute", top: "80px", width: "100%", zIndex: 1000 }}>
        <EmergencyButton onClick={handleEmergencyClick} />
      </div>
    </div>
  );
};

export default EmergencyMap;
