import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Popup, useMap } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
const center = [17.385044, 78.486671]; // Hyderabad coordinates (example)

const HospitalMarker = ({ hospital }) => (
  <Marker position={[hospital.lat, hospital.lon]}>
    <Popup>{hospital.display_name}</Popup>
  </Marker>
);

const EmergencyMap = () => {
  const [hospitals, setHospitals] = useState([]);

  const searchHospitals = async () => {
    const response = await fetch(
      `https://nominatim.openstreetmap.org/search?format=json&q=hospitals+near+${center[0]},${center[1]}`
    );
    const data = await response.json();
    setHospitals(data);
  };

  return (
    <>
      <button className="search-btn" onClick={searchHospitals}>Search Hospitals</button>
      <MapContainer center={center} zoom={13} style={{ height: "80vh", width: "100%" }}>
        <TileLayer
          attribution='&copy; <a href="https://osm.org/copyright">OpenStreetMap</a>'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        {hospitals.map((hospital, index) => (
          <HospitalMarker key={index} hospital={hospital} />
        ))}
      </MapContainer>
    </>
  );
};

export default EmergencyMap;
